mapper.select:
select
*,timestampdiff(MONTH, birth_date, sysdate()) months
, datediff(sysdate(), birth_date) + 1 days

and user_id = #{userId}
order by user_id desc
limit #{start}, #{count}

mapper.insert:
        insert into child
        (
        user_id
        , name
        , sex
        , birth_date
        , image_url
        , stature
        , weight
        , head_circumference
        , status
        , reg_datetime
        , mod_datetime
        )
        values
        (
        #{userId}
        , #{name}
        , #{sex}
        , #{birthDate}
        , #{imageUrl}
        , #{stature}
        , #{weight}
        , #{headCircumference}
        , 'S'
        , sysdate()
        , sysdate()
        )

        
            select last_insert_id()

mapper.update:
update user
set
name = #{name}
, sex = #{email}
, birth_date = #{birthDate}
, image_url = #{imageUrl}
, stature = #{stature}
, weight = #{weight}
, head_circumference = #{headCircumference}
, status = #{status}
, mod_datetime = sysdate()
where 1 = 1
and child_id = #{childId}

mapper.delete:
delete from child
where 1 = 1
and child_id = #{childId}
